                // Creates the drop-dowin 
                function buildComboLinePrepDropDown(gn_prod_id , cur_prep_id)
                {
                // Find this item in the menu_items_master first
                var item_master = null ;
                if (gn_prod_id) // It could for a new line
                {
                    for (var i in menu_items_master)
